import React, { Component } from 'react';
import Datas from '../../../data/home/hero-slider.json';
import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import Swiper from 'react-id-swiper';
import { Styles } from "../styles/heroSlider.js";

// Popup Component
class OverlayPopup extends Component {
    render() {
        const { isOpen, onClose } = this.props;

        if (!isOpen) return null;

        return (
            <div style={styles.overlay}>
                <div style={styles.popup}>
                    <h1 style={styles.title}>🎓 Welcome!</h1>
                    <p style={styles.text}>We're glad you're here. Explore the best university experience with us.</p>
                    <button style={styles.button} onClick={onClose}>Got it!</button>
                </div>
            </div>
        );
    }
}

class HeroSlider extends Component {
    constructor(props) {
        super(props);
        this.state = {
            popupOpened: true  // always show on page load
        };
    }

    handleClosePopup = () => {
        this.setState({ popupOpened: false });
    };

    render() {
        const settings = {
            slidesPerView: 1,
            loop: true,
            speed: 3000,
            autoplay: {
                delay: 3000,
                disableOnInteraction: false
            },
            watchSlidesVisibility: true,
            effect: 'fade',
            navigation: {
                nextEl: '.slider-button-next',
                prevEl: '.slider-button-prev'
            },
            renderPrevButton: () => (
                <div className="swiper-btn slider-button-prev"><i className="flaticon-arrow-left-th"></i></div>
            ),
            renderNextButton: () => (
                <div className="swiper-btn slider-button-next"><i className="flaticon-arrow-right-th"></i></div>
            )
        };

        return (
            <Styles>
                {/* Popup Overlay */}
                <OverlayPopup isOpen={this.state.popupOpened} onClose={this.handleClosePopup} />

                {/* Hero Slider */}
                <section className="hero-slider-area">
                    <Swiper {...settings}>
                        {
                            Datas.map((data, i) => (
                                <div className="slider-item" key={i}>
                                    <div className="image-container">
                                        <img src={process.env.PUBLIC_URL + `/assets/images/${data.backgroundImage}`} className="slider-image" alt={data.backgroundImage} />
                                    </div>
                                    <div className="slider-table">
                                        <div className="slider-tablecell">
                                            <Container>
                                                <Row>
                                                    <Col md="12">
                                                        <div className={data.uniqClass}>
                                                            <div className="slider-title">
                                                                <p>{data.title}</p>
                                                            </div>
                                                            <div className="slider-desc">
                                                                <h1>{data.desc}</h1>
                                                            </div>
                                                            <div className="slider-btn">
                                                                <Link className="slider-btn1" to={process.env.PUBLIC_URL + `/${data.btnOneLink}`}>Our Courses</Link>
                                                                <Link className="slider-btn2" to={process.env.PUBLIC_URL + `/${data.btnTwoLink}`}>Contact Us</Link>
                                                            </div>
                                                        </div>
                                                    </Col>
                                                </Row>
                                            </Container>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                    </Swiper>
                </section>
            </Styles>
        );
    }
}

export default HeroSlider;

// Updated large popup styles
const styles = {
    overlay: {
        position: 'fixed',
        top: 0, left: 0, right: 0, bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.75)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 9999,
        padding: '20px'
    },
    popup: {
        background: '#ffffff',
        padding: '60px 40px',
        borderRadius: '16px',
        maxWidth: '600px',
        width: '100%',
        textAlign: 'center',
        boxShadow: '0 10px 30px rgba(0, 0, 0, 0.3)',
        animation: 'fadeIn 0.3s ease-in-out'
    },
    title: {
        fontSize: '2.5rem',
        marginBottom: '20px',
        fontWeight: '700'
    },
    text: {
        fontSize: '1.25rem',
        marginBottom: '30px'
    },
    button: {
        fontSize: '1rem',
        padding: '12px 30px',
        border: 'none',
        backgroundColor: '#28a745',
        color: '#fff',
        borderRadius: '8px',
        cursor: 'pointer'
    }
};
