import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Container, Row, Col, Tab, Nav } from 'react-bootstrap';
import HeaderTwo from '../../components/common/Header';
import { BreadcrumbBox } from '../../components/common/Breadcrumb';
import FooterTwo from '../../components/FooterTwo';
import { Styles } from './styles/course';
import Datas from '../../data/course/instructor.json';
import Pagination from './../../components/Pagination';
import courseData from '../../data/course/courseDescription.json';

function CourseDetails() {
    const { id } = useParams();
    const [course, setCourse] = useState(null);

    useEffect(() => {
        const selectedCourse = courseData.find(course => course.id === id);
        setCourse(selectedCourse);
    }, [id]);

    useEffect(() => {
        const courseButton = document.querySelectorAll(".course-button");
        courseButton.forEach(button => {
            button.addEventListener("click", () => {
                button.classList.toggle("active");
                const content = button.nextElementSibling;

                if (button.classList.contains("active")) {
                    content.className = "course-content show";
                    content.style.maxHeight = content.scrollHeight + "px";
                } else {
                    content.className = "course-content";
                    content.style.maxHeight = "0";
                }
            });
        });
    });

    if (!course) return <p>Loading...</p>;

    return (
        <div className="main-wrapper course-details-page">
            <HeaderTwo />
            <BreadcrumbBox title="Course Details" />
            <Styles>
                <section className="course-details-area">
                    <Container>
                        <Row>
                            <Col lg="12" md="8" sm="12">
                                <div className="course-details-top">
                                    <div className="heading">
                                        <h4>{course.heading}</h4>
                                    </div>
                                    <div className="course-top-overview">
                                        <div className="d-flex overviews">
                                            <div className="author">
                                                <img src={`${process.env.PUBLIC_URL}/assets/images/${course.authorProfile}`} alt="" />
                                                <div className="author-name">
                                                    <h6>Author</h6>
                                                    <p>{course.authorName}</p>
                                                </div>
                                            </div>
                                            <div className="category">
                                                <h6>Category</h6>
                                                <p>{course.category}</p>
                                            </div>
                                            <div className="rating">
                                                <h6>Rating</h6>
                                                <ul className="list-unstyled list-inline">
                                                    {[...Array(Math.floor(course.rating))].map((_, i) => (
                                                        <li key={i} className="list-inline-item"><i className="las la-star"></i></li>
                                                    ))}
                                                    {course.rating % 1 !== 0 && <li className="list-inline-item"><i className="las la-star-half-alt"></i></li>}
                                                    <li className="list-inline-item">({course.rating})</li>
                                                </ul>
                                            </div>
                                            <div className="price">
                                                <h6>Price</h6>
                                                <p>{course.price}</p>
                                            </div>
                                        </div>
                                    </div>
                                    <div className="course-details-banner">
                                        <img src={`${process.env.PUBLIC_URL}/assets/images/${course.banner}`} alt="" className="img-fluid" />
                                    </div>
                                    <div className="course-tab-list">
                                        <Tab.Container defaultActiveKey="overview">
                                            <Nav className="flex-column">
                                                <Nav.Item>
                                                    <Nav.Link eventKey="overview">Overview</Nav.Link>
                                                </Nav.Item>
                                                <Nav.Item>
                                                    <Nav.Link eventKey="curriculum">Faculty</Nav.Link>
                                                </Nav.Item>
                                                <Nav.Item>
                                                    <Nav.Link eventKey="instructor">Achievements</Nav.Link>
                                                </Nav.Item>
                                                <Nav.Item>
                                                    <Nav.Link eventKey="review">Apply Now</Nav.Link>
                                                </Nav.Item>
                                            </Nav>
                                            <Tab.Content>
                                                <Tab.Pane eventKey="overview" className="overview-tab">
                                                    <div className="course-desc">
                                                        <h5>Course Description</h5>
                                                        <p>{course.overview.courseDesc}</p>
                                                    </div>
                                                    <div className="course-feature">
                                                        <h5>Course Feature</h5>
                                                        <p>{course.overview.courseFeature}</p>
                                                        <ul className="list-unstyled">
                                                            {course.overview.featureList.map((item, i) => (
                                                                <li key={i}><i className="las la-arrow-right"></i> {item}</li>
                                                            ))}
                                                        </ul>
                                                    </div>
                                                    <div className="course-learn">
                                                        <h5>Learning Outcome</h5>
                                                        <p>{course.overview.courseLearn}</p>
                                                        <ul className="list-unstyled">
                                                            {course.overview.learnList.map((item, i) => (
                                                                <li key={i}><i className="fa fa-check"></i> {item}</li>
                                                            ))}
                                                        </ul>
                                                    </div>
                                                    {course.overview.courseShare && (
                                                        <div className="course-share">
                                                            <h5>Share This Course</h5>
                                                            <ul className="social list-unstyled list-inline">
                                                                <li className="list-inline-item"><a href="/"><i className="fab fa-facebook-f"></i></a></li>
                                                                <li className="list-inline-item"><a href="/"><i className="fab fa-twitter"></i></a></li>
                                                                <li className="list-inline-item"><a href="/"><i className="fab fa-linkedin-in"></i></a></li>
                                                                <li className="list-inline-item"><a href="/"><i className="fab fa-youtube"></i></a></li>
                                                                <li className="list-inline-item"><a href="/"><i className="fab fa-dribbble"></i></a></li>
                                                            </ul>
                                                        </div>
                                                    )}
                                                </Tab.Pane>
                                                
                                            </Tab.Content>
                                        </Tab.Container>
                                    </div>
                                </div>
                            </Col>
                        </Row>
                    </Container>
                </section>
            </Styles>
            <FooterTwo />
        </div>
    );
}

export default CourseDetails;
