import React, { Component } from 'react';
import Datas from '../../../data/home/hero-slider.json';
import { Link } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import Swiper from 'react-id-swiper';
import { Styles } from "../styles/heroSlider.js";

// Overlay Popup Component
class OverlayPopup extends Component {
    render() {
        const { isOpen, onClose } = this.props;

        if (!isOpen) return null;

        return (
            <div style={styles.overlay}>
                <div style={styles.popup}>
                    <h2>Welcome!</h2>
                    <p>This popup appears only the first time you visit.</p>
                    <button style={styles.button} onClick={onClose}>Close</button>
                </div>
            </div>
        );
    }
}

class HeroSlider extends Component {
    constructor(props) {
        super(props);
        this.state = {
            popupOpened: false
        };
    }

    componentDidMount() {
        const hasShown = localStorage.getItem('popupShown');
        if (!hasShown) {
            this.setState({ popupOpened: true });
            localStorage.setItem('popupShown', 'true');
        }
    }

    handleClosePopup = () => {
        this.setState({ popupOpened: false });
    };

    render() {
        const settings = {
            slidesPerView: 1,
            loop: true,
            speed: 3000,
            autoplay: {
                delay: 3000,
                disableOnInteraction: false
            },
            watchSlidesVisibility: true,
            effect: 'fade',
            navigation: {
                nextEl: '.slider-button-next',
                prevEl: '.slider-button-prev'
            },
            renderPrevButton: () => (
                <div className="swiper-btn slider-button-prev"><i className="flaticon-arrow-left-th"></i></div>
            ),
            renderNextButton: () => (
                <div className="swiper-btn slider-button-next"><i className="flaticon-arrow-right-th"></i></div>
            )
        };

        return (
            <Styles>
                {/* Popup Overlay */}
                <OverlayPopup isOpen={this.state.popupOpened} onClose={this.handleClosePopup} />

                {/* Hero Slider */}
                <section className="hero-slider-area">
                    <Swiper {...settings}>
                        {
                            Datas.map((data, i) => (
                                <div className="slider-item" key={i}>
                                    <div className="image-container">
                                        <img src={process.env.PUBLIC_URL + `/assets/images/${data.backgroundImage}`} className="slider-image" alt={data.backgroundImage} />
                                    </div>
                                    <div className="slider-table">
                                        <div className="slider-tablecell">
                                            <Container>
                                                <Row>
                                                    <Col md="12">
                                                        <div className={data.uniqClass}>
                                                            <div className="slider-title">
                                                                <p>{data.title}</p>
                                                            </div>
                                                            <div className="slider-desc">
                                                                <h1>{data.desc}</h1>
                                                            </div>
                                                            <div className="slider-btn">
                                                                <Link className="slider-btn1" to={process.env.PUBLIC_URL + `/${data.btnOneLink}`}>Our Courses</Link>
                                                                <Link className="slider-btn2" to={process.env.PUBLIC_URL + `/${data.btnTwoLink}`}>Contact Us</Link>
                                                            </div>
                                                        </div>
                                                    </Col>
                                                </Row>
                                            </Container>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                    </Swiper>
                </section>
            </Styles>
        );
    }
}

export default HeroSlider;

// Basic inline styles for overlay popup
const styles = {
    overlay: {
        position: 'fixed',
        top: 0, left: 0, right: 0, bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.6)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 9999
    },
    popup: {
        background: '#fff',
        padding: '30px',
        borderRadius: '10px',
        maxWidth: '400px',
        textAlign: 'center',
        boxShadow: '0 4px 20px rgba(0,0,0,0.3)'
    },
    button: {
        marginTop: '20px',
        padding: '10px 20px',
        border: 'none',
        background: '#007bff',
        color: '#fff',
        borderRadius: '5px',
        cursor: 'pointer'
    }
};
