// FILE: src/components/common/HeroSlider.js

import React, { Component } from 'react';
import Datas from '../../../data/home/hero-slider.json';
import popupData from '../../../data/pop-up/popupDetails.json';
import secondPopupData from '../../../data/pop-up/secondpopup.json';
import { Link, withRouter } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import Swiper from 'react-id-swiper';
import { Styles } from '../styles/heroSlider.js';

// Overlay Popup with vertical infinite scroll
class OverlayPopup extends Component {
    scrollRef = React.createRef();
    scrollInterval = null;

    componentDidMount() {
        const { minimal, data } = this.props;
        if (minimal && data.length > 1) {
            // Duplicate the content to allow seamless infinite scroll
            this.duplicateScrollContent();
            this.startScrolling();
        }
    }

    componentWillUnmount() {
        clearInterval(this.scrollInterval);
    }

    duplicateScrollContent = () => {
        if (this.scrollRef.current) {
            const container = this.scrollRef.current;
            container.innerHTML += container.innerHTML;
        }
    };

    startScrolling = () => {
        const container = this.scrollRef.current;
        if (!container) return;

        let scrollTop = 0;
        this.scrollInterval = setInterval(() => {
            scrollTop += 1;
            if (scrollTop >= container.scrollHeight / 2) {
                scrollTop = 0;
            }
            container.scrollTo({
                top: scrollTop,
                behavior: 'smooth'
            });
        }, 30); // Adjust speed here
    };

    render() {
        const { isOpen, onClose, data, onSubmit, minimal = false } = this.props;
        if (!isOpen) return null;

        return (
            <div className="popup-overlay">
                <div className={`popup-box ${minimal ? 'minimal' : ''}`}>
                    <button className="popup-close-btn" onClick={onClose}>âœ–</button>

                    {!minimal ? (
                        <>
                            <h1 className="popup-title">{data.title}</h1>
                            <p className="popup-text">{data.description}</p>
                            {data.image && (
                                <img
                                    src={process.env.PUBLIC_URL + data.image}
                                    alt="Popup"
                                    className="popup-image"
                                />
                            )}
                            <button className="popup-submit-btn" onClick={onSubmit}>{data.button}</button>
                        </>
                    ) : (
                        <div
                            className="scroll-container"
                            ref={this.scrollRef}
                        >
                            {data.map((item, idx) => (
                                <div key={idx} className="scroll-entry">
                                    <h2 className="popup-title">{item.title}</h2>
                                    <p className="popup-text">{item.description}</p>
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>
        );
    }
}

class HeroSlider extends Component {
    state = {
        popupOneOpened: true,
        popupTwoOpened: false
    };

    handleCloseFirstPopup = () => {
        this.setState({ popupOneOpened: false, popupTwoOpened: true });
    };

    handleCloseSecondPopup = () => {
        this.setState({ popupTwoOpened: false });
    };

    handlePopupSubmit = () => {
        this.props.history.push(`${process.env.PUBLIC_URL}/course`);
    };

    render() {
        const settings = {
            slidesPerView: 1,
            loop: true,
            speed: 3000,
            autoplay: {
                delay: 3000,
                disableOnInteraction: false
            },
            effect: 'fade',
            navigation: {
                nextEl: '.slider-button-next',
                prevEl: '.slider-button-prev'
            },
            renderPrevButton: () => (
                <div className="swiper-btn slider-button-prev"><i className="flaticon-arrow-left-th"></i></div>
            ),
            renderNextButton: () => (
                <div className="swiper-btn slider-button-next"><i className="flaticon-arrow-right-th"></i></div>
            )
        };

        return (
            <Styles>
                <OverlayPopup
                    isOpen={this.state.popupOneOpened}
                    onClose={this.handleCloseFirstPopup}
                    onSubmit={this.handlePopupSubmit}
                    data={popupData}
                />
                <OverlayPopup
                    isOpen={this.state.popupTwoOpened}
                    onClose={this.handleCloseSecondPopup}
                    data={secondPopupData}
                    minimal={true}
                />

                <section className="hero-slider-area">
                    <Swiper {...settings}>
                        {Datas.map((data, i) => (
                            <div className="slider-item" key={i}>
                                <div className="image-container">
                                    <img src={process.env.PUBLIC_URL + `/assets/images/${data.backgroundImage}`} className="slider-image" alt={data.backgroundImage} />
                                </div>
                                <div className="slider-table">
                                    <div className="slider-tablecell">
                                        <Container>
                                            <Row>
                                                <Col md="12">
                                                    <div className={data.uniqClass}>
                                                        <div className="slider-title">
                                                            <p>{data.title}</p>
                                                        </div>
                                                        <div className="slider-desc">
                                                            <h1>{data.desc}</h1>
                                                        </div>
                                                        <div className="slider-btn">
                                                            <Link className="slider-btn1" to={process.env.PUBLIC_URL + `/${data.btnOneLink}`}>Our Courses</Link>
                                                            <Link className="slider-btn2" to={process.env.PUBLIC_URL + `/${data.btnTwoLink}`}>Contact Us</Link>
                                                        </div>
                                                    </div>
                                                </Col>
                                            </Row>
                                        </Container>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </Swiper>
                </section>
            </Styles>
        );
    }
}

export default withRouter(HeroSlider);
