// src/components/Home/components/PopupWelcome.js

import React, { useState, useEffect } from 'react';
import { Modal, Box, Typography, IconButton } from '@mui/material';
import CloseIcon from '@mui/icons-material/Close';
import { popupWelcomeStyles as styles } from '../';

const PopupWelcome = () => {
  const [open, setOpen] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => setOpen(false), 10000);
    return () => clearTimeout(timer);
  }, []);

  const handleClose = () => setOpen(false);

  return (
    <Modal
      open={open}
      onClose={handleClose}
      aria-labelledby="welcome-title"
      aria-describedby="welcome-description"
    >
      <Box sx={styles.modalBox}>
        <IconButton sx={styles.closeBtn} onClick={handleClose}>
          <CloseIcon />
        </IconButton>
        <img
          src="https://img.mobiscroll.com/demos/logo-noshadow.jpg"
          alt="Logo"
          style={styles.logo}
        />
        <Typography id="welcome-title" variant="h5" component="h2" gutterBottom>
          ðŸŽ“ <span role="img" aria-label="Graduation Cap">ðŸŽ“</span> Welcome to Our Lady College
        </Typography>
        <Typography id="welcome-description">
          Explore our campus, programs, and community today!
        </Typography>
      </Box>
    </Modal>
  );
};

export default PopupWelcome;
