import React, { useState, useEffect } from 'react';
import './PopupWelcome';

const PopupWelcome = () => {
  const [isVisible, setIsVisible] = useState(true);

  const closePopup = () => {
    setIsVisible(false);
  };

  useEffect(() => {
    const timer = setTimeout(() => setIsVisible(false), 10000); // auto-close after 10s
    return () => clearTimeout(timer);
  }, []);

