import styled from "styled-components";


export const Styles = styled.div`
    .hero-slider-area {
        // your existing styles
    }

    .popup-overlay {
        position: fixed;
        top: 0; left: 0; right: 0; bottom: 0;
        background-color: rgba(0, 0, 0, 0.8);
        display: flex;
        justify-content: center;
        align-items: center;
        z-index: 9999;
        padding: 20px;
    }

    .popup-box {
        position: relative;
        background: #ffffff;
        padding: 60px 50px;
        border-radius: 16px;
        max-width: 800px;
        width: 100%;
        min-height: 400px;
        text-align: center;
        box-shadow: 0 15px 40px rgba(0, 0, 0, 0.35);
        overflow: hidden;
    }

    .popup-box.minimal {
        padding: 30px;
        min-height: 200px;
        max-height: 400px;
    }

    .popup-title {
        font-size: 1.8rem;
        margin-bottom: 10px;
        font-weight: bold;
    }

    .popup-text {
        font-size: 1.1rem;
        margin-bottom: 20px;
    }

    .popup-image {
        width: 100%;
        max-height: 450px;
        object-fit: cover;
        border-radius: 12px;
        margin-bottom: 20px;
    }

    .popup-submit-btn {
        font-size: 1rem;
        padding: 14px 34px;
        border: none;
        background-color: #007bff;
        color: #fff;
        border-radius: 8px;
        cursor: pointer;
    }

    .popup-close-btn {
        position: absolute;
        top: 10px;
        right: 15px;
        background: none;
        border: none;
        font-size: 2rem;
        cursor: pointer;
        color: #000;
    }

    .scroll-wrapper {
        height: 100%;
        overflow: hidden;
        position: relative;
    }

    .scroll-content {
        display: flex;
        flex-direction: column;
        animation: scrollVertical 15s linear infinite;
    }

    .scroll-entry {
    padding-bottom: 20px;
    border-bottom: 1px solid #eee;
    margin-bottom: 20px; /* ✅ Adds gap below the horizontal line */
    }

    .scroll-entry .popup-title {
        margin-bottom: 8px; /* ✅ Adds gap between title and the description */
    }


    @keyframes scrollVertical {
        0% {
            transform: translateY(0%);
        }
        100% {
            transform: translateY(-50%);
        }
    }
`;
