import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Container, Row, Col } from "react-bootstrap";
import instructorDetails from "../../data/course";

function InstructorDetails() {
  const { name } = useParams(); // this 'name' is actually the 'slug' now
  const [instructor, setInstructor] = useState(null);

  useEffect(() => {
    const foundInstructor = instructorDetails.find(
      (inst) => inst.slug === name.toLowerCase()
    );
    setInstructor(foundInstructor);
  }, [name]);

  if (!instructor) {
    return (
      <Container className="my-5">
        <h2>Instructor not found</h2>
      </Container>
    );
  }

  return (
    <section className="instructor-details-area">
      <Container>
        <Row>
          <Col md="4">
            <div className="instructor-img text-center">
              <img
                src={`${process.env.PUBLIC_URL}/assets/images/${instructor.image}`}
                alt={instructor.name}
                className="img-fluid rounded"
              />
            </div>
          </Col>
          <Col md="8">
            <div className="instructor-info">
              <h2>{instructor.name}</h2>
              <h5>{instructor.title}</h5>
              <p>{instructor.description}</p>
              <ul className="list-inline">
                <li className="list-inline-item">
                  <a
                    href={instructor.socialLinks.facebook}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <i className="fab fa-facebook-f"></i>
                  </a>
                </li>
                <li className="list-inline-item">
                  <a
                    href={instructor.socialLinks.twitter}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <i className="fab fa-twitter"></i>
                  </a>
                </li>
                <li className="list-inline-item">
                  <a
                    href={instructor.socialLinks.linkedin}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <i className="fab fa-linkedin"></i>
                  </a>
                </li>
                <li className="list-inline-item">
                  <a
                    href={instructor.socialLinks.youtube}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <i className="fab fa-youtube"></i>
                  </a>
                </li>
              </ul>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
}

export default InstructorDetails;
