import React, { Component } from 'react';
import Datas from '../../../data/home/hero-slider.json';
import popupData from '../../../data/pop-up/popupDetails.json';
import { Link, withRouter } from 'react-router-dom';
import { Container, Row, Col } from 'react-bootstrap';
import Swiper from 'react-id-swiper';
import { Styles } from "../styles/heroSlider.js";

// Overlay Popup Component
class OverlayPopup extends Component {
    render() {
        const { isOpen, onClose, data, onSubmit } = this.props;

        if (!isOpen) return null;

        return (
            <div style={styles.overlay}>
                <div style={styles.popup}>
                    <button style={styles.closeBtn} onClick={onClose}>âœ–</button>
                    <h1 style={styles.title}>{data.title}</h1>
                    <p style={styles.text}>{data.description}</p>
                     {/* Display image if available */}
                    {data.image && (
                        <img
                            src={process.env.PUBLIC_URL + data.image}
                            alt="Popup"
                            style={styles.image}
                        />
                    )}
                    <button style={styles.submitBtn} onClick={onSubmit}>{data.button}</button>
                </div>
            </div>
        );
    }
}


class HeroSlider extends Component {
    constructor(props) {
        super(props);
        this.state = {
            popupOpened: true
        };
    }

    handleClosePopup = () => {
        this.setState({ popupOpened: false });
    };

    handlePopupSubmit = () => {
        // Navigate to Courses page
        this.props.history.push(`${process.env.PUBLIC_URL}/course`);
    };

    render() {
        const settings = {
            slidesPerView: 1,
            loop: true,
            speed: 3000,
            autoplay: {
                delay: 3000,
                disableOnInteraction: false
            },
            watchSlidesVisibility: true,
            effect: 'fade',
            navigation: {
                nextEl: '.slider-button-next',
                prevEl: '.slider-button-prev'
            },
            renderPrevButton: () => (
                <div className="swiper-btn slider-button-prev"><i className="flaticon-arrow-left-th"></i></div>
            ),
            renderNextButton: () => (
                <div className="swiper-btn slider-button-next"><i className="flaticon-arrow-right-th"></i></div>
            )
        };

        return (
            <Styles>
                {/* Overlay Popup */}
                <OverlayPopup
                    isOpen={this.state.popupOpened}
                    onClose={this.handleClosePopup}
                    onSubmit={this.handlePopupSubmit}
                    data={popupData}
                />

                {/* Hero Slider */}
                <section className="hero-slider-area">
                    <Swiper {...settings}>
                        {
                            Datas.map((data, i) => (
                                <div className="slider-item" key={i}>
                                    <div className="image-container">
                                        <img src={process.env.PUBLIC_URL + `/assets/images/${data.backgroundImage}`} className="slider-image" alt={data.backgroundImage} />
                                    </div>
                                    <div className="slider-table">
                                        <div className="slider-tablecell">
                                            <Container>
                                                <Row>
                                                    <Col md="12">
                                                        <div className={data.uniqClass}>
                                                            <div className="slider-title">
                                                                <p>{data.title}</p>
                                                            </div>
                                                            <div className="slider-desc">
                                                                <h1>{data.desc}</h1>
                                                            </div>
                                                            <div className="slider-btn">
                                                                <Link className="slider-btn1" to={process.env.PUBLIC_URL + `/${data.btnOneLink}`}>Our Courses</Link>
                                                                <Link className="slider-btn2" to={process.env.PUBLIC_URL + `/${data.btnTwoLink}`}>Contact Us</Link>
                                                            </div>
                                                        </div>
                                                    </Col>
                                                </Row>
                                            </Container>
                                        </div>
                                    </div>
                                </div>
                            ))
                        }
                    </Swiper>
                </section>
            </Styles>
        );
    }
}

export default withRouter(HeroSlider); // Enables routing props like `history`

// Styling for overlay popup
const styles = {
    overlay: {
        position: 'fixed',
        top: 0, left: 0, right: 0, bottom: 0,
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        zIndex: 9999,
        padding: '20px'
    },
    popup: {
        position: 'relative',
        background: '#ffffff',
        padding: '60px 50px',
        borderRadius: '16px',
        maxWidth: '800px',    // Increased from 600px
        width: '100%',
        minHeight: '400px',   // Added to increase height
        textAlign: 'center',
        boxShadow: '0 15px 40px rgba(0, 0, 0, 0.35)'
    },
    title: {
        fontSize: '2.5rem',   // Slightly increased for larger popup
        marginBottom: '20px',
        fontWeight: 'bold'
    },
    text: {
        fontSize: '1.3rem',
        marginBottom: '30px'
    },
    submitBtn: {
        fontSize: '1rem',
        padding: '14px 34px',
        border: 'none',
        backgroundColor: '#007bff',
        color: '#fff',
        borderRadius: '8px',
        cursor: 'pointer'
    },
    closeBtn: {
        position: 'absolute',
        top: '10px',
        right: '15px',
        background: 'none',
        border: 'none',
        fontSize: '2rem',
        cursor: 'pointer',
        color: '#000'
    },
    image: {
    width: '100%',
    maxHeight: '100%',
    objectFit: 'cover',
    borderRadius: '12px',
    marginBottom: '20px'
}
};
