// Home.js
import React, { Component, useState } from 'react';
import HeroSlider from './components/Home/components/HeroSlider';
import IconBox from './components/IconBox';
import AboutUs from './components/AboutUs';
import TestimonialSlider from './components/Home/components/TestimonialSlider';
import TeamSlider from './components/Home/components/TeamSlider';
import HomeBlog from './components/Home/components/HomeBlog';
import CampusTour from './components/Home/components/CampusTour';
import NewsletterForm from './components/Home/components/NewsletterForm';
import Footer from './components/Footer';
import HeaderTwo from './components/common/Header';

// Mobiscroll Imports
import { Button, Page, Popup, setOptions } from '@mobiscroll/react-lite';
import '@mobiscroll/react-lite/dist/css/mobiscroll.min.css';

setOptions({
  // Optional theme or locale settings
});

function CenterPopup() {
  const [isCenterOpen, setCenterOpen] = useState(false);

  return (
    <div style={{ textAlign: 'center', marginBottom: '20px' }}>
      <Button onClick={() => setCenterOpen(true)}>Show Welcome Message</Button>

      <Popup display="center" isOpen={isCenterOpen} onClose={() => setCenterOpen(false)}>
        <div className="mbsc-align-center mbsc-padding">
          <img src="https://img.mobiscroll.com/demos/logo-noshadow.jpg" alt="logo" />
          <h4>Welcome to Our Lady College!</h4>
          <p>Explore our campus, team, and latest updates.</p>
        </div>
      </Popup>
    </div>
  );
}

export default class Home extends Component {
  render() {
    return (
      <div className="main-wrapper">

        {/* Header */}
        <HeaderTwo />

        {/* Hero Slider */}
        <HeroSlider />

        {/* Center Display Popup Button */}
        <Page>
          <CenterPopup />
        </Page>

        {/* Icon Box */}
        <IconBox />

        {/* About Area */}
        <AboutUs />

        {/* Blog Area */}
        <HomeBlog />

        {/* Testimonial Slider */}
        <TestimonialSlider />

        {/* Team Slider */}
        <TeamSlider />

        {/* Campus Tour */}
        <CampusTour />

        {/* Newsletter Form */}
        <NewsletterForm />

        {/* Footer */}
        <Footer />
      </div>
    );
  }
}
