// PopupWelcome.js
import React, { useState } from 'react';
import { Button, Page, Popup, setOptions } from '@mobiscroll/react-lite';
import '@mobiscroll/react-lite/dist/css/mobiscroll.min.css';

setOptions({
  // Customize options if needed (theme, locale, etc.)
});

const PopupWelcome = () => {
  const [isCenterOpen, setCenterOpen] = useState(false);

  return (
    <Page>
      <div style={{ textAlign: 'center', margin: '30px 0' }}>
        <Button onClick={() => setCenterOpen(true)}>ðŸŽ‰ Click to See Welcome Message</Button>
      </div>

      <Popup display="center" isOpen={isCenterOpen} onClose={() => setCenterOpen(false)}>
        <div className="mbsc-align-center mbsc-padding">
          <img
            src="https://img.mobiscroll.com/demos/logo-noshadow.jpg"
            alt="logo"
            style={{ maxWidth: '100px', marginBottom: '15px' }}
          />
          <h4>Welcome to Our Lady College!</h4>
          <p>Explore courses, connect with faculty, and enjoy your campus experience!</p>
        </div>
      </Popup>
    </Page>
  );
};

export default PopupWelcome;
