// heroSlider.js
import styled from "styled-components";
import { colors } from "../../common/element/elements.js";

export const Styles = styled.div`
    /* Hero Slider Styles Omitted for Brevity (same as your code)... */

    /* Popup Styles */
    .popup-overlay {
        position: fixed;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        background-color: rgba(0, 0, 0, 0.8);
        display: flex;
        justify-content: center;
        align-items: center;
        z-index: 9999;
        padding: 20px;
    }

    .popup-box {
        position: relative;
        background: #ffffff;
        padding: 60px 50px;
        border-radius: 16px;
        max-width: 800px;
        width: 100%;
        min-height: 400px;
        text-align: center;
        box-shadow: 0 15px 40px rgba(0, 0, 0, 0.35);

        &.minimal {
            padding: 40px 30px;
            overflow: hidden;

            .scroll-container {
                height: 320px;
                overflow: hidden;
                position: relative;
            }

            .scroll-content {
                display: flex;
                flex-direction: column;
                animation: scrollLoop 20s linear infinite;
            }

            .scroll-entry {
                flex: 0 0 auto;
                margin-bottom: 40px;

                .popup-title {
                    font-size: 1.8rem;
                    margin-bottom: 10px;
                }

                .popup-text {
                    font-size: 1.1rem;
                }
            }
        }
    }

    @keyframes scrollLoop {
        0% { transform: translateY(0); }
        100% { transform: translateY(-50%); }
