// FILE: src/components/styles/heroSlider.js

import styled from "styled-components";

export const Styles = styled.div`
    .hero-slider-area {
        // Your existing slider styles
    }

    .popup-overlay {
        position: fixed;
        top: 0; left: 0; right: 0; bottom: 0;
        background-color: rgba(0, 0, 0, 0.8);
        display: flex;
        justify-content: center;
        align-items: center;
        z-index: 9999;
        padding: 20px;
    }

    .popup-box {
        position: relative;
        background: #fff;
        padding: 60px 50px;
        border-radius: 16px;
        max-width: 800px;
        width: 100%;
        height: 500px;
        text-align: center;
        box-shadow: 0 15px 40px rgba(0, 0, 0, 0.35);
        overflow: hidden;
    }

    .popup-box.minimal {
        padding: 30px;
        height: 350px;
    }

    .popup-title {
        font-size: 1.6rem;
        font-weight: bold;
        margin-bottom: 5px;
    }

    .popup-text {
        font-size: 1rem;
        margin-bottom: 10px;
    }

    .popup-image {
        width: 100%;
        max-height: 300px;
        object-fit: cover;
        border-radius: 12px;
        margin-bottom: 20px;
    }

    .popup-submit-btn {
        font-size: 1rem;
        padding: 14px 34px;
        border: none;
        background-color: #007bff;
        color: #fff;
        border-radius: 8px;
        cursor: pointer;
    }

    .popup-close-btn {
        position: absolute;
        top: 10px;
        right: 15px;
        background: none;
        border: none;
        font-size: 2rem;
        cursor: pointer;
        color: #000;
    }

    .scroll-container {
        height: 100%;
        overflow-y: hidden;
        scroll-behavior: smooth;
    }

    .scroll-entry {
        padding: 20px 10px;
        border-bottom: 1px solid #eee;
    }
`;
